import "./App.css";
import PicCarousel from "./components/Carousel";
import Cart from "./components/Cart";
import { useState, useEffect } from "react";
import Details from './components/Details'
import AddToCart from './components/AddToCart'

function App() {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [info, setInfo] = useState([]);
  const [cart, setCart] = useState([]);
  useEffect(() => {
    async function fetchImg() {
      let call = await fetch(
        `http://${window.location.hostname}:3000/api/product/image/1`
      );
      let data = await call.json();
      setImages(data);
    }
    async function fetchProduct(){
      let call = await fetch(
        `http://${window.location.hostname}:3000/api/product/1`
      );
      let data = await call.json();
      setInfo(data)
    }
    async function fetchCart(){
      let call = await fetch(
        `http://${window.location.hostname}:3000/api/cart/1`
        );
        let data = await call.json();
        setCart(data);
    }
    fetchImg();
    fetchProduct();
    fetchCart();
    setLoading(false);
  }
  , []);
  if (!loading) {
    return (
      <div className="App">
        <Details info={info}/>
        <PicCarousel images={images}/>
        <Cart images={images} cart={cart}/>
      </div>
    );
  }
}

export default App;
