  const [loading, setLoading] = useState(true);
  useEffect(() => {
      async function apiCall() {
        let call = await fetch(`http://${window.location.hostname}:3000/api/product/image/1`);
        let data = await call.json();
        setImages(data)
    }
    apiCall()
  }, []);
  return (
    <div className="App">
      <PicCarousel images={images}/>
      <Cart images={images}/>
    </div>
  );
}

