import "./App.css";
import PicCarousel from "./components/Carousel";
import Cart from "./components/Cart";
import { useState, useEffect } from "react";

function App() {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [info, setInfo] = useState([]);
  useEffect(() => {
    async function fetchImg() {
      let call = await fetch(
        `http://${window.location.hostname}:3000/api/product/image/1`
      );
      let data = await call.json();
      setImages(data);
    }
    async function fetchProduct(){
      let call = await fetch(
        `http://${window.location.hostname}:3000/api/product/1`
      );
      let data = await call.json();
      setInfo(data)
      setLoading(false);
    }
    async function fetchCart(){
      let call = await fetch(`http://${window.location.hostname}:3000/api/cart/1`)
    }
    fetchImg();
    fetchProduct();
  }
  , []);
  if (!loading) {
    return (
      <div className="App">
        <PicCarousel images={images}/>
        <Cart images={images} info={info}/>
      </div>
    );
  }
}

export default App;
