import React, { useState } from 'react'
import { Carousel } from 'react-responsive-carousel';
import "react-responsive-carousel/lib/styles/carousel.min.css";

let data;
const apiCall = async () => {
    let call = await fetch(`http://${window.location.hostname}:3000/api/product/image/1`)
    data = await call.json();
}
apiCall()

function carousel() {
  return (
    <Carousel className="pic">
        {data.map(data => {<div><img>{data.image_url}</img></div>})}
    </Carousel>
  )
}

export default carousel
